package main

import (
	"encoding/base64"
	"encoding/binary"
	"fmt"
	"net"
)

func main() {
	// connect to this socket
	conn, _ := net.Dial("tcp", "127.0.0.1:123")

	bs := make([]byte, 4)
	binary.LittleEndian.PutUint32(bs, 1)
	conn.Write(bs)

	sDec, _ := base64.StdEncoding.DecodeString("")

	data := make([]byte, 1605)
	data[0] = 7
	copy(data[1:], sDec)

	fmt.Println(sDec)

	for i := 1; i < 1000000; i++ {
		conn.Write(data)
	}

	conn.Close()
}