package curl

import (
    "encoding/base64"
    "github.com/iotadevelopment/go/ternary"
    "testing"
)

func BenchmarkSimpleHash(b *testing.B) {
    transactionBytes, _ := base64.StdEncoding.DecodeString("")
    trits := ternary.BytesToTrits(transactionBytes)[:8019]

    var resp = make(ternary.Trinary, CURLP81_HASH_LENGTH)

    b.ResetTimer()
    for n := 0; n < b.N; n++ {
        curl := NewCurl(CURLP81_HASH_LENGTH, CURLP81_ROUNDS)
        curl.Absorb(trits, 0, len(trits))
        curl.Squeeze(resp, 0, CURLP81_HASH_LENGTH)
    }
}

func BenchmarkBatchHash(b *testing.B) {
    transactionBytes, _ := base64.StdEncoding.DecodeString("")
    trits := ternary.BytesToTrits(transactionBytes)[:8019]

    collectedChannels := make([]chan ternary.Trinary, b.N)

    b.ResetTimer()
    for n := 0; n < b.N; n++ {
        collectedChannels[n] = CURLP81.Hash(trits)
    }
    for _, collectedChannel := range collectedChannels {
        <- collectedChannel
    }
}